mapper.select:
select * from product


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by id desc

mapper.update:
update product
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, batching = #{batching},
where id = #{id}
